mapper.select:

select id
from member
where id = #{userId}
  and pwd = #{userPwd}

mapper.update:
        
        update savedword
        set wordcount = #{wordCount}, savedDate = #{savedDate}
        where user_id = #{user_id} and wordId =#{wordId}

        
        update savedword
        set wordcount = #{wordCount}, savedDate = #{savedDate}, isNewcard = false
        where user_id = #{user_id} and wordId =#{wordId}
