mapper.update:

update savedword
set wordcount   = #{wordCount},
    appearanceDate   = #{appearanceDate},
    isWrongcard = true
where userId = #{userId}
  and wordId = #{wordId}


update savedword
set wordcount   = #{wordCount},
    appearanceDate   = #{appearanceDate},
    isWrongcard = false
where userId = #{userId}
  and wordId = #{wordId}
